import { useState } from "react"
import { useDispatch } from "react-redux";
import { addSong } from "./redux/reducers/songReducer";
import "./SongForm.css"
const SongForm = () => {
    const [priority, setPriority] = useState(1);
    const dispatch = useDispatch();
    let song = {  songName: "",
                    url: "",
                    source: 0,
                    priority: priority}
    return(
        <div>
            <form className="addSongForm">
                <input type="text" placeholder ="Název písně" onChange={(e) => {song.songName = e.target.value}}></input>
                <input type="text" placeholder ="Url" onChange={(e) => {song.url = e.target.value}}></input>
                <div>
                <input type="radio" id="sourceYouTube" name="source" value ="YouTube" defaultChecked onChange={(e) => {song.source = 0}}></input>
                <label htmlFor="sourceYouTube">YouTube</label>
                </div>
                <div>
                <input type="radio" id="sourceSpotify" name="source" value ="Spotify" onChange={(e) => {song.source = 1}}></input>
                <label htmlFor="sourceSpotify">Spotify</label>
                </div>
                <label>Priorita: {priority}</label>
                <input type="range" id="priority" min="1" max="5" defaultValue="1" onChange={(e) => setPriority(e.target.value)}></input>
                <input type="button"value="Přidat píseň" onClick={() => dispatch(addSong(song))}></input>
            </form>

        </div>
    )
}
export default SongForm
